INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES
('-1616000', 'Lesser beings, intruding here! A shame that your excess courage does not compensate for your stupidity!', NULL, 'Des êtres inférieurs qui s\'introduisent ici ! Dommage que votre témérité ne rattrape pas votre bêtise !', NULL, NULL, NULL, NULL, NULL, NULL, '14512', '1', '0', '457', NULL),
('-1616001', 'None but the blue dragonflight are welcome here! Perhaps this is the work of Alexstrasza? Well then, she has sent you to your deaths.', NULL, 'Seuls les membres du Vol Bleu sont les bienvenus ici ! Serait-ce l\'oeuvre d\'Alexstrasza ? Dans ce cas elle vous aura envoyé à la mort !', NULL, NULL, NULL, NULL, NULL, NULL, '14513', '1', '0', '457', NULL),
('-1616002', 'What could you hope to accomplish, to storm brazenly into my domain? To employ MAGIC? Against ME? <Laughs>', NULL, 'Qu\'est ce que vous espériez donc ? Prendre d\'assaut mon domaine ? Employer la magie contre moi ? MOHAHAHAHHAHAHAHAHHAAHAHA !', NULL, NULL, NULL, NULL, NULL, NULL, '14514', '1', '0', '457', NULL),
('-1616003', 'I am without limits here... the rules of your cherished reality do not apply... In this realm, I am in control...', NULL, 'Ici je ne connais pas de limites... Les lois de votre chère réalité ne s\'appliquent pas... Dans ce royaume, c\'est moi qui commande...', NULL, NULL, NULL, NULL, NULL, NULL, '14515', '1', '0', '457', NULL),
('-1616004', 'I give you one chance. Pledge fealty to me, and perhaps I won''t slaughter you for your insolence!', NULL, 'Je vous laisse une chance. Prêtez-moi allégeance et peut-être que je ne vous massacrerez pas pour votre impudence !', NULL, NULL, NULL, NULL, NULL, NULL, '14516', '1', '0', '457', NULL),
('-1616005', 'My patience has reached its limit, I WILL BE RID OF YOU!', NULL, 'Ma patience à ses limites, JE VAIS ME DÉBARRASSER DE VOUS !', NULL, NULL, NULL, NULL, NULL, NULL, '14517', '1', '0', '1', NULL),
('-1616006', 'Watch helplessly as your hopes are swept away...', NULL, 'Voyez tous vos espoirs se faire balayer...', NULL, NULL, NULL, NULL, NULL, NULL, '14525', '1', '0', '1', NULL),
('-1616007', 'I AM UNSTOPPABLE!', NULL, 'RIEN NE PEUT M\'ARRÊTER !', NULL, NULL, NULL, NULL, NULL, NULL, '14533', '1', '0', '1', NULL),
('-1616008', 'Your stupidity has finally caught up to you!', NULL, 'Votre bêtise à fini par vous rattraper !', NULL, NULL, NULL, NULL, NULL, NULL, '14519', '1', '0', '1', NULL),
('-1616009', 'More artifacts to confiscate...', NULL, 'Encore des artéfacts à confisquer...', NULL, NULL, NULL, NULL, NULL, NULL, '14520', '1', '0', '1', NULL),
('-1616010', '<Laughs> How very... naive...', NULL, '<Rires> C\'est touchant... de naïveté...', NULL, NULL, NULL, NULL, NULL, NULL, '14521', '1', '0', '1', NULL),
('-1616012', 'I had hoped to end your lives quickly, but you have proven more...resilient then I had anticipated. Nonetheless, your efforts are in vain, it is you reckless, careless mortals who are to blame for this war! I do what I must...And if it means your...extinction...THEN SO BE IT!', NULL, 'Je pensais mettre fin rapidement à vos existences mais vous avez fait preuve de plus de résilience que prévu. Quoi qu\il en soit, vos efforts sont vains ! C\'est vous mortels imprudents et suicidaires qui êtes responsable de cette guerre ! Je fais ce que je dois et si cela signifie votre extinction... Ainsi soit-il!', NULL, NULL, NULL, NULL, NULL, NULL, '14522', '1', '0', '1', NULL),
('-1616013', 'Few have experienced the pain I will now inflict upon you!', NULL, 'Peu de gens ont connu la douleur que je vais vous infliger !', NULL, NULL, NULL, NULL, NULL, NULL, '14523', '1', '0', '1', NULL),
('-1616014', 'YOU WILL NOT SUCCEED WHILE I DRAW BREATH!', NULL, 'Vous n\'arriverez à rien tant qu\il me restera un SOUFFLE !', NULL, NULL, NULL, NULL, NULL, NULL, '14518', '1', '0', '1', NULL),
('-1616015', 'Malygos takes a deep breath...', NULL, 'Malygos inspire profondément...', NULL, NULL, NULL, NULL, NULL, NULL, '0', '3', '0', '1', NULL),
('-1616016', 'I will teach you IGNORANT children just how little you know of magic...', NULL, 'Enfants ignorants ! Je vais vous montrais à quel point vous ne connaissez rien de la magie...', NULL, NULL, NULL, NULL, NULL, NULL, '14524', '1', '0', '1', NULL),
('-1616017', 'ENOUGH! If you intend to reclaim Azeroth\'s magic, then you shall have it...', NULL, 'ASSEZ ! Si c\'est la magie d\'Azeroth que vous voulez alors vous l\'aurez !', NULL, NULL, NULL, NULL, NULL, NULL, '14529', '1', '0', '1', NULL),
('-1616018', 'Now your benefactors make their appearance...But they are too late. The powers contained here are sufficient to destroy the world ten times over! What do you think they will do to you?', NULL, 'Vos bienfaiteurs font enfin leur entrée... Mais il est trop tard ! Les puissances contenues ici sont amplement suffisantes pour détruire 10 fois le monde ! Que croyez-vous qu\'elles feront de vous ?', NULL, NULL, NULL, NULL, NULL, NULL, '14530', '1', '0', '1', NULL),
('-1616019', 'SUBMIT!', NULL, 'SOUMETTEZ-VOUS !', NULL, NULL, NULL, NULL, NULL, NULL, '14531', '1', '0', '1', NULL),
('-1616020', 'Your energy will be put to good use!', NULL, 'Il sera fait bon usage de votre énergie !', NULL, NULL, NULL, NULL, NULL, NULL, '14526', '1', '0', '1', NULL),
('-1616021', 'I AM THE SPELL-WEAVER! My power is INFINITE!', NULL, 'Je suis le TISSE-SORT ! Ma puissance est INFINIE !', NULL, NULL, NULL, NULL, NULL, NULL, '14527', '1', '0', '1', NULL),
('-1616022', 'Your spirit will linger here forever!', NULL, 'Votre esprit va languir ici pour toujours !', NULL, NULL, NULL, NULL, NULL, NULL, '14528', '1', '0', '1', NULL),
('-1616023', 'Alexstrasza! Another of your brood falls!', NULL, 'Alexstrasza! Vois tomber un autre membre de ta couvée !', NULL, NULL, NULL, NULL, NULL, NULL, '14534', '1', '0', '1', NULL),
('-1616024', 'Little more then gnats!', NULL, 'A peine plus qu\'un moustique !', NULL, NULL, NULL, NULL, NULL, NULL, '14535', '1', '0', '1', NULL),
('-1616025', 'Your red allies will share your fate...', NULL, 'Vos alliés rouges partageront votre destin...', NULL, NULL, NULL, NULL, NULL, NULL, '14536', '1', '0', '1', NULL),
('-1616026', 'The powers at work here exceed anything you could possibly imagine!', NULL, 'Les puissances à l\'oeuvre ici dépasse tout ce que vous pourriez imaginer !', NULL, NULL, NULL, NULL, NULL, NULL, '14532', '1', '0', '1', NULL),
('-1616027', 'Still standing? Not for long...', NULL, 'Encore debout ? Plus pour longtemps...', NULL, NULL, NULL, NULL, NULL, NULL, '14537', '1', '0', '1', NULL),
('-1616028', 'Your cause is lost!', NULL, 'Votre cause est perdu !', NULL, NULL, NULL, NULL, NULL, NULL, '14538', '1', '0', '1', NULL),
('-1616029', 'Your fragile mind will be shattered!', NULL, NULL, 'Vos frêles esprits vont être fracassés', NULL, NULL, NULL, NULL, NULL, '14539', '1', '0', '1', NULL),
('-1616030', 'UNTHINKABLE! The mortals will destroy... e-everything... my sister... what have you-', NULL, 'Impensable ! Les mortels vont tout détruire... Ma soeur... qu\'est ce que tu as...', NULL, NULL, NULL, NULL, NULL, NULL, '14540', '1', '0', '1', NULL),
('-1616031', 'I did what I had to, brother. You gave me no alternative.', NULL, 'J\'ai fais ce que j\'avais à faire mon frère. Tu ne m\'as laissé aucune alternative.', NULL, NULL, NULL, NULL, NULL, NULL, '14406', '1', '0', '1', NULL),
('-1616032', 'And so ends the Nexus War.', NULL, 'Et ainsi s\'achève la guerre du Nexus', NULL, NULL, NULL, NULL, NULL, NULL, '14407', '1', '0', '1', NULL),
('-1616033', 'This resolution pains me deeply, but the destruction, the monumental loss of life had to end. Regardless of Malygos\' recent transgressions, I will mourn his loss. He was once a guardian, a protector. This day, one of the world\'s mightiest has fallen.', NULL, 'Cette résolution m\'attriste énormément, mais la destruction, le gâchis monumental de vie devait cesser. Malgré les transgressions récentes de Malygos, je pleurerai sa perte.', NULL, NULL, NULL, NULL, NULL, NULL, '14408', '1', '0', '1', NULL),
('-1616034', 'The red dragonflight will take on the burden of mending the devastation wrought on Azeroth. Return home to your people and rest. Tomorrow will bring you new challenges, and you must be ready to face them. Life...goes on.', NULL, 'Le vol Draconnique rouge assumera le fardeau de la réparation des dégâts causés sur Azeroth. Rentrez dans vos foyers, et reposez-vous. Demain vous apportera de nouveaux défis auxquels vous devrez faire face.', NULL, NULL, NULL, NULL, NULL, NULL, '14409', '1', '0', '1', NULL),
('-1616035', 'A Power Spark forms from a nearby rift!', NULL, 'Près d\'une crevasse proche une étincelle de puissance se forme !', NULL, NULL, NULL, NULL, NULL, NULL, '0', '3', '0', '1', NULL);